cssContent:
/* Glass Theme - Ultra glassmorphism with soft glow */
[data-cyber-theme="glass"] {
    --theme-primary: var(--neon-primary, #00d4ff);
    --theme-glow: var(--glow-soft, 0 0 12px rgba(0, 212, 255, 0.4));
    --theme-border: rgba(255, 255, 255, 0.1);
    --glass-blur: var(--glass-blur-ultra, blur(32px));

    /* Enhanced glass surfaces */
    --bg-surface: rgba(15, 20, 30, 0.3);
    --bg-surface-light: rgba(20, 25, 35, 0.2);

    /* Accent colors */
    --accent: #7dd3fc;
    --accent-muted: #38bdf8;
    --accent-dark: #0ea5e9;

    /* Backgrounds (Glass Specific) */
    --bg-primary: #0a0e17; /* Keep dark base */
    --bg-secondary: rgba(15, 23, 42, 0.4);
    --bg-card: rgba(255, 255, 255, 0.05);
    --bg-card-elevated: rgba(255, 255, 255, 0.08);

    /* High contrast text - Light for glass */
    --text-primary: #ffffff;
    --text-secondary: #e0f2fe;
    --text-muted: #bae6fd;

    /* Borders */
    --border-color: rgba(255, 255, 255, 0.1);
    --border-color-hover: rgba(255, 255, 255, 0.2);

    /* Admin semantic variables */
    --admin-panel-bg: rgba(255, 255, 255, 0.08);
    --admin-panel-border: rgba(255, 255, 255, 0.15);
    --admin-card-bg: rgba(255, 255, 255, 0.08);
    --admin-input-bg: rgba(0, 0, 0, 0.2);
    --admin-text-primary: #ffffff;
    --admin-text-secondary: #cbd5e1;
}

/* Glass mode panel styling - translucent with heavy blur */
[data-cyber-theme="glass"] .panel-cyber,
[data-cyber-theme="glass"] .card-cyber {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.05), rgba(255, 255, 255, 0.01));
    backdrop-filter: blur(24px);
    -webkit-backdrop-filter: blur(24px);
    border: 1px solid rgba(255, 255, 255, 0.08);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.37), inset 0 0 0 1px rgba(255, 255, 255, 0.02);
}

[data-cyber-theme="glass"] .panel-cyber:hover,
[data-cyber-theme="glass"] .card-cyber:hover {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.08), rgba(255, 255, 255, 0.02));
    border-color: rgba(255, 255, 255, 0.15);
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.45), 0 0 20px rgba(125, 211, 252, 0.2);
    transform: translateY(-6px);
}

/* Glass mode text styling - soft glow */
[data-cyber-theme="glass"] .text-theme-primary {
    color: #7dd3fc;
}

[data-cyber-theme="glass"] .text-theme-glow {
    color: #7dd3fc;
    text-shadow: 0 0 20px rgba(125, 211, 252, 0.5);
}

/* Glass mode button styling - translucent */
[data-cyber-theme="glass"] .btn-theme {
    background: linear-gradient(135deg, rgba(125, 211, 252, 0.3), rgba(125, 211, 252, 0.1));
    backdrop-filter: blur(12px);
    color: white;
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 0 20px rgba(125, 211, 252, 0.2);
}

[data-cyber-theme="glass"] .btn-theme:hover {
    background: linear-gradient(135deg, rgba(125, 211, 252, 0.4), rgba(125, 211, 252, 0.2));
    box-shadow: 0 0 30px rgba(125, 211, 252, 0.4);
    transform: translateY(-2px);
}

/* Glass mode special - frosted glass effect */
[data-cyber-theme="glass"] .glass-frost {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px) saturate(180%);
    -webkit-backdrop-filter: blur(20px) saturate(180%);
}
