/* Neon Cyber Theme - Maximum intensity glow */
[data-cyber-theme="neon"] {
    --theme-primary: var(--neon-cyan, #00ffff);
    --theme-glow: var(--glow-strong, 0 0 24px rgba(0, 212, 255, 0.9));
    --theme-border: var(--border-neon-glow, rgba(0, 212, 255, 0.8));
    --glass-blur: var(--glass-blur-light, blur(8px));

    /* Admin semantic variables */
    --admin-panel-bg: rgba(20, 30, 40, 0.85);
    --admin-panel-border: rgba(0, 255, 255, 0.4);
    --admin-card-bg: rgba(20, 30, 40, 0.85);
    --admin-input-bg: rgba(0, 0, 0, 0.6);
    --admin-text-primary: #e0f2fe;
    --admin-text-secondary: #bae6fd;
}

/* Neon mode panel styling - enhanced glow */
[data-cyber-theme="neon"] .panel-cyber,
[data-cyber-theme="neon"] .card-cyber {
    background: var(--bg-surface, rgba(15, 20, 30, 0.65));
    border: 1px solid var(--neon-primary, #00d4ff);
    box-shadow: 0 0 20px rgba(0, 255, 255, 0.5), 0 8px 32px rgba(0, 0, 0, 0.5);
}

[data-cyber-theme="neon"] .panel-cyber:hover,
[data-cyber-theme="neon"] .card-cyber:hover {
    border-color: var(--neon-cyan, #00ffff);
    box-shadow: 0 0 30px rgba(0, 255, 255, 0.7), 0 0 60px rgba(0, 212, 255, 0.3), 0 12px 40px rgba(0, 0, 0, 0.6);
    transform: translateY(-8px);
}

/* Neon mode text styling - strong glow */
[data-cyber-theme="neon"] .text-theme-primary {
    color: var(--neon-cyan, #00ffff);
}

[data-cyber-theme="neon"] .text-theme-glow {
    color: var(--neon-cyan, #00ffff);
    text-shadow: 0 0 10px rgba(0, 255, 255, 0.8), 0 0 20px rgba(0, 255, 255, 0.5), 0 0 30px rgba(0, 255, 255, 0.3);
}

/* Neon mode button styling - intense glow */
[data-cyber-theme="neon"] .btn-theme {
    background: linear-gradient(135deg, #00ffff 0%, #00d4ff 100%);
    color: #0a0e17;
    border: none;
    box-shadow: 0 0 20px rgba(0, 255, 255, 0.5);
}

[data-cyber-theme="neon"] .btn-theme:hover {
    box-shadow: 0 0 30px rgba(0, 255, 255, 0.7);
    transform: translateY(-3px);
}

/* Neon mode special accents */
[data-cyber-theme="neon"] .accent-glow {
    animation: neon-pulse 2s ease-in-out infinite;
}

@keyframes neon-pulse {
    0%, 100% { box-shadow: 0 0 10px rgba(0, 255, 255, 0.5); }
    50% { box-shadow: 0 0 30px rgba(0, 255, 255, 0.9); }
}
