/* Normal Cyber Theme - Balanced neon glow */
[data-cyber-theme="normal"] {
    --theme-primary: var(--neon-primary, #00d4ff);
    --theme-glow: var(--glow-medium, 0 0 16px rgba(0, 212, 255, 0.7));
    --theme-border: var(--border-neon, rgba(0, 212, 255, 0.2));
    --glass-blur: var(--glass-blur-medium, blur(16px));

    /* Admin semantic variables */
    --admin-panel-bg: rgba(30, 41, 59, 0.65);
    --admin-panel-border: rgba(0, 212, 255, 0.2);
    --admin-card-bg: rgba(30, 41, 59, 0.65);
    --admin-input-bg: rgba(10, 15, 25, 0.8);
    --admin-text-primary: #e0e7ff;
    --admin-text-secondary: #94a3b8;
}

/* Normal mode panel styling */
[data-cyber-theme="normal"] .panel-cyber,
[data-cyber-theme="normal"] .card-cyber {
    background: var(--bg-surface, rgba(15, 20, 30, 0.65));
    border: 1px solid var(--border-neon, rgba(0, 212, 255, 0.2));
    box-shadow: var(--shadow-neon-md, 0 4px 16px rgba(0, 0, 0, 0.4), 0 0 12px rgba(0, 212, 255, 0.3));
}

[data-cyber-theme="normal"] .panel-cyber:hover,
[data-cyber-theme="normal"] .card-cyber:hover {
    border-color: var(--border-neon-bright, rgba(0, 212, 255, 0.5));
    box-shadow: var(--shadow-neon-lg, 0 8px 32px rgba(0, 0, 0, 0.5), 0 0 20px rgba(0, 212, 255, 0.4));
}

/* Normal mode text styling */
[data-cyber-theme="normal"] .text-theme-primary {
    color: var(--neon-primary, #00d4ff);
}

[data-cyber-theme="normal"] .text-theme-glow {
    color: var(--neon-primary, #00d4ff);
    text-shadow: var(--glow-medium, 0 0 16px rgba(0, 212, 255, 0.7));
}

/* Normal mode button styling */
[data-cyber-theme="normal"] .btn-theme {
    background: linear-gradient(135deg, #00d4ff 0%, #00ffff 100%);
    color: #0a0e17;
    border: none;
    box-shadow: 0 0 12px rgba(0, 212, 255, 0.4);
}

[data-cyber-theme="normal"] .btn-theme:hover {
    box-shadow: 0 0 20px rgba(0, 212, 255, 0.6);
    transform: translateY(-2px);
}
