cssContent:
/* Professional Theme - Polished dark with teal accents */
[data-cyber-theme="professional"] {
    /* Primary accent colors */
    --theme-primary: #00D4FF;
    --theme-glow: 0 0 8px rgba(0, 212, 255, 0.3);
    --theme-border: rgba(51, 65, 85, 0.6);
    --glass-blur: blur(12px);

    /* Accent colors */
    --accent: #00D4FF;
    --accent-muted: #06B6D4;
    --accent-dark: #0891B2;

    /* Backgrounds */
    --bg-primary: #0A0E17;
    --bg-secondary: #0F172A;
    --bg-card: rgba(15, 23, 42, 0.85);
    --bg-card-elevated: rgba(30, 41, 59, 0.9);

    /* High contrast text */
    --text-primary: #F1F5F9;
    --text-secondary: #94A3B8;
    --text-muted: #64748B;

    /* Borders */
    --border-color: rgba(51, 65, 85, 0.6);
    --border-color-hover: rgba(0, 212, 255, 0.4);

    /* Admin variables */
    --admin-panel-bg: rgba(15, 23, 42, 0.9);
    --admin-panel-border: rgba(51, 65, 85, 0.6);
    --admin-card-bg: rgba(30, 41, 59, 0.85);
    --admin-input-bg: rgba(10, 15, 25, 0.9);
    --admin-text-primary: #F1F5F9;
    --admin-text-secondary: #94A3B8;
}

/* ============================================
   Tab Control Styling
   ============================================ */

[data-cyber-theme="professional"] .tab-control {
    --tab-container-bg: rgba(15, 23, 42, 0.85);
    --tab-container-border: rgba(51, 65, 85, 0.6);
    --tab-bg: transparent;
    --tab-bg-hover: rgba(0, 212, 255, 0.1);
    --tab-bg-active: #00D4FF;
    --tab-text: #94A3B8;
    --tab-text-hover: #F1F5F9;
    --tab-text-active: #0A0E17;
    --tab-shadow-active: 0 2px 8px rgba(0, 212, 255, 0.3);
}

/* ============================================
   Dashboard Stat Cards
   ============================================ */

[data-cyber-theme="professional"] .stat-card {
    background: rgba(15, 23, 42, 0.85);
    border: 1px solid rgba(51, 65, 85, 0.6);
    border-radius: 1rem;
    padding: 1.5rem;
}

[data-cyber-theme="professional"] .stat-card:hover {
    border-color: rgba(0, 212, 255, 0.5);
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.3);
}

[data-cyber-theme="professional"] .stat-label {
    color: #94A3B8;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

[data-cyber-theme="professional"] .stat-value {
    color: #F1F5F9;
    font-size: 2rem;
    font-weight: 700;
}

/* ============================================
   Timeline / Experience Cards
   ============================================ */

[data-cyber-theme="professional"] .timeline-card {
    background: rgba(15, 23, 42, 0.85);
    border: 1px solid rgba(51, 65, 85, 0.6);
    border-radius: 1rem;
    padding: 2rem;
}

[data-cyber-theme="professional"] .timeline-card:hover {
    border-color: #00D4FF;
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.4);
}

/* Date Badge - Teal filled */
[data-cyber-theme="professional"] .timeline-card-date {
    background: #00D4FF;
    color: #0A0E17;
    padding: 0.375rem 0.75rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 600;
}

[data-cyber-theme="professional"] .timeline-card-date[data-current="true"] {
    background: #10B981;
    color: #FFFFFF;
}

/* Type Badge - Teal outline */
[data-cyber-theme="professional"] .timeline-type-badge {
    padding: 0.375rem 0.75rem;
    font-size: 0.6875rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    border-radius: 0.375rem;
    background: transparent;
    border: 1px solid;
}

[data-cyber-theme="professional"] .timeline-type-badge[data-type="experience"] {
    color: #00D4FF;
    border-color: #00D4FF;
}

[data-cyber-theme="professional"] .timeline-type-badge[data-type="education"] {
    color: #10B981;
    border-color: #10B981;
}

[data-cyber-theme="professional"] .timeline-type-badge[data-type="achievement"] {
    color: #F59E0B;
    border-color: #F59E0B;
}

/* Card Content Text */
[data-cyber-theme="professional"] .timeline-card-title {
    font-size: 1.25rem;
    font-weight: 700;
    color: #FFFFFF;
}

[data-cyber-theme="professional"] .timeline-card-org {
    font-size: 0.9375rem;
    color: #94A3B8;
}

[data-cyber-theme="professional"] .timeline-card-org svg {
    color: #64748B;
}

[data-cyber-theme="professional"] .timeline-card-desc {
    font-size: 0.9375rem;
    color: #CBD5E1;
    line-height: 1.75;
}

/* Technology Badges */
[data-cyber-theme="professional"] .timeline-card-skills {
    border-top: 1px solid rgba(51, 65, 85, 0.6);
    padding-top: 1.25rem;
    margin-top: 1.25rem;
}

[data-cyber-theme="professional"] .timeline-card-skills .tech-badge,
[data-cyber-theme="professional"] .tech-badge {
    padding: 0.375rem 0.75rem;
    font-size: 0.8125rem;
    font-weight: 500;
    color: #F1F5F9;
    background: rgba(30, 41, 59, 0.8);
    border: 1px solid rgba(148, 163, 184, 0.4);
    border-radius: 0.375rem;
}

[data-cyber-theme="professional"] .timeline-card-skills .tech-badge:hover,
[data-cyber-theme="professional"] .tech-badge:hover {
    border-color: #00D4FF;
    background: rgba(0, 212, 255, 0.1);
    color: #FFFFFF;
}

/* ============================================
   Skill Category Cards
   ============================================ */

[data-cyber-theme="professional"] .resume-skill-category {
    background: rgba(15, 23, 42, 0.85);
    border: 1px solid rgba(51, 65, 85, 0.6);
    border-radius: 1rem;
}

[data-cyber-theme="professional"] .resume-skill-category:hover {
    border-color: #00D4FF;
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.4);
}

[data-cyber-theme="professional"] .resume-skill-category-header {
    border-bottom: 1px solid rgba(51, 65, 85, 0.6);
    padding: 1.25rem;
}

[data-cyber-theme="professional"] .resume-skill-category-name {
    font-size: 1.125rem;
    font-weight: 700;
    color: #F1F5F9;
}

[data-cyber-theme="professional"] .resume-skill-item {
    padding: 0.375rem 0.75rem;
    background: rgba(30, 41, 59, 0.8);
    border: 1px solid rgba(148, 163, 184, 0.4);
    border-radius: 0.375rem;
    font-size: 0.8125rem;
    font-weight: 500;
    color: #F1F5F9;
}

[data-cyber-theme="professional"] .resume-skill-item:hover {
    border-color: #00D4FF;
    background: rgba(0, 212, 255, 0.1);
}

/* ============================================
   Language Cards
   ============================================ */

[data-cyber-theme="professional"] .resume-language-card {
    background: rgba(15, 23, 42, 0.85);
    border: 1px solid rgba(51, 65, 85, 0.6);
    border-radius: 1rem;
    padding: 1.5rem;
}

[data-cyber-theme="professional"] .resume-language-card:hover {
    border-color: #00D4FF;
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.4);
}

[data-cyber-theme="professional"] .resume-language-card .language-name {
    font-size: 1.25rem;
    font-weight: 700;
    color: #F1F5F9;
}

[data-cyber-theme="professional"] .resume-language-card .skill-label {
    color: #94A3B8;
}

/* ============================================
   Domain Cards
   ============================================ */

[data-cyber-theme="professional"] .resume-domain-card {
    background: rgba(15, 23, 42, 0.85);
    border: 1px solid rgba(51, 65, 85, 0.6);
    border-radius: 1rem;
    padding: 1.5rem;
}

[data-cyber-theme="professional"] .resume-domain-card:hover {
    border-color: #00D4FF;
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.4);
}

[data-cyber-theme="professional"] .resume-domain-card .domain-icon {
    width: 48px;
    height: 48px;
    background: rgba(0, 212, 255, 0.15);
    border: 1px solid rgba(0, 212, 255, 0.3);
    border-radius: 0.75rem;
    color: #00D4FF;
}

[data-cyber-theme="professional"] .resume-domain-card .domain-name {
    font-size: 1.25rem;
    font-weight: 700;
    color: #F1F5F9;
}

[data-cyber-theme="professional"] .resume-domain-card .domain-years {
    color: #94A3B8;
}

[data-cyber-theme="professional"] .resume-domain-card .domain-level {
    background: #00D4FF;
    color: #0A0E17;
    padding: 0.375rem 1rem;
    border-radius: 9999px;
    font-size: 0.6875rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

[data-cyber-theme="professional"] .resume-domain-card .domain-description {
    color: #94A3B8;
    border-top: 1px solid rgba(51, 65, 85, 0.6);
    padding-top: 0.75rem;
    margin-top: 0.75rem;
}

/* ============================================
   Hobby Cards
   ============================================ */

[data-cyber-theme="professional"] .resume-hobby-card {
    background: rgba(15, 23, 42, 0.85);
    border: 1px solid rgba(51, 65, 85, 0.6);
    border-radius: 1rem;
    padding: 1.25rem;
}

[data-cyber-theme="professional"] .resume-hobby-card:hover {
    border-color: #00D4FF;
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.4);
}

[data-cyber-theme="professional"] .resume-hobby-card .hobby-icon {
    width: 48px;
    height: 48px;
    background: rgba(0, 212, 255, 0.15);
    border: 1px solid rgba(0, 212, 255, 0.3);
    color: #00D4FF;
    border-radius: 0.75rem;
}

[data-cyber-theme="professional"] .resume-hobby-card .hobby-name {
    font-size: 1.125rem;
    font-weight: 700;
    color: #F1F5F9;
}

[data-cyber-theme="professional"] .resume-hobby-card .hobby-description {
    color: #94A3B8;
}

/* ============================================
   Project Cards
   ============================================ */

[data-cyber-theme="professional"] .project-card {
    background: rgba(15, 23, 42, 0.85);
    border: 1px solid rgba(51, 65, 85, 0.6);
    border-radius: 1rem;
}

[data-cyber-theme="professional"] .project-card:hover {
    border-color: #00D4FF;
    box-shadow: 0 16px 48px rgba(0, 0, 0, 0.5);
}

[data-cyber-theme="professional"] .project-card-status,
[data-cyber-theme="professional"] .project-card-footer .tech-label {
    color: #00D4FF;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

[data-cyber-theme="professional"] .tech-more {
    color: #00D4FF;
    background: rgba(0, 212, 255, 0.1);
}

/* ============================================
   Hero Summary Card
   ============================================ */

[data-cyber-theme="professional"] .resume-summary {
    background: rgba(15, 23, 42, 0.85);
    border: 1px solid rgba(51, 65, 85, 0.6);
    border-radius: 1.5rem;
    padding: 2.5rem;
    color: #94A3B8;
}

/* ============================================
   Buttons
   ============================================ */

[data-cyber-theme="professional"] .btn-theme {
    background: linear-gradient(135deg, #00D4FF 0%, #06B6D4 100%);
    color: #0A0E17;
    border: none;
    box-shadow: 0 2px 8px rgba(0, 212, 255, 0.3);
    font-weight: 600;
}

[data-cyber-theme="professional"] .btn-theme:hover {
    box-shadow: 0 4px 16px rgba(0, 212, 255, 0.4);
    transform: translateY(-2px);
}

/* ============================================
   Panels and Cards - Generic
   ============================================ */

[data-cyber-theme="professional"] .panel-cyber,
[data-cyber-theme="professional"] .card-cyber {
    background: rgba(15, 23, 42, 0.85);
    border: 1px solid rgba(51, 65, 85, 0.6);
    box-shadow: 0 4px 24px rgba(0, 0, 0, 0.4);
    backdrop-filter: blur(12px);
}

[data-cyber-theme="professional"] .panel-cyber:hover,
[data-cyber-theme="professional"] .card-cyber:hover {
    border-color: #00D4FF;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.5);
}

/* ============================================
   Text Utilities
   ============================================ */

[data-cyber-theme="professional"] .text-theme-primary {
    color: #00D4FF;
}

[data-cyber-theme="professional"] .text-theme-glow {
    color: #00D4FF;
    text-shadow: 0 0 12px rgba(0, 212, 255, 0.4);
}

/* ============================================
   Disable aggressive animations
   ============================================ */

[data-cyber-theme="professional"] .shimmer-neon,
[data-cyber-theme="professional"] .glow-pulse {
    animation: none;
}

[data-cyber-theme="professional"] .resume-animate-in {
    animation: professionalFadeIn 0.4s ease-out forwards;
}

@keyframes professionalFadeIn {
    from {
        opacity: 0;
        transform: translateY(12px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}
