/**
 * @fileoverview Asset Type Declarations
 * Provides TypeScript module declarations for static assets imported in tests.
 * 
 * @author Ark.Alliance
 */

// Image file declarations
declare module '*.png' {
    const content: string;
    export default content;
}

declare module '*.jpg' {
    const content: string;
    export default content;
}

declare module '*.jpeg' {
    const content: string;
    export default content;
}

declare module '*.gif' {
    const content: string;
    export default content;
}

declare module '*.svg' {
    const content: string;
    export default content;
}

declare module '*.webp' {
    const content: string;
    export default content;
}

// Font file declarations
declare module '*.woff' {
    const content: string;
    export default content;
}

declare module '*.woff2' {
    const content: string;
    export default content;
}

declare module '*.ttf' {
    const content: string;
    export default content;
}

declare module '*.eot' {
    const content: string;
    export default content;
}
